import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

interface Employee {
  employeeId: string;
  employeeName: string;
  address: string;
  city: string;
  createdBy: string;
}

@Component({
  selector: 'app-employee-form',
  templateUrl: './employee-form.component.html',
  styleUrls: ['./employee-form.component.scss']
})
export class EmployeeFormComponent {


  employeeForm!: FormGroup;
  employees: Employee[] = [];

  constructor(private fb: FormBuilder) {}

  ngOnInit(): void {
    this.employeeForm = this.fb.group({
      employeeId: ['', Validators.required],
      employeeName: ['', Validators.required],
      address: ['', Validators.required],
      city: ['', Validators.required],
      createdBy: ['', Validators.required],
    });
  }

  submit() {
    if (this.employeeForm.invalid) {
      this.employeeForm.markAllAsTouched();
      return;
    }

    const employeeData = this.employeeForm.value;
    this.employees.push(employeeData);

    this.employeeForm.reset();
  }
}
<div class="container">

  <!-- Employee Form -->
  <form [formGroup]="employeeForm" (ngSubmit)="submit()" class="employee-form">

    <h2>Employee Details</h2>

    <div class="row">

      <div class="form-group">
        <label>Employee ID</label>
        <input type="text" formControlName="employeeId">
      </div>

      <div class="form-group">
        <label>Employee Name</label>
        <input type="text" formControlName="employeeName">
      </div>

      <div class="form-group">
        <label>Address</label>
        <input type="text" formControlName="address">
      </div>

      <div class="form-group">
        <label>City</label>
        <input type="text" formControlName="city">
      </div>

      <div class="form-group">
        <label>Created By</label>
        <input type="text" formControlName="createdBy">
      </div>

    </div>

    <button type="submit" class="submit-btn">Submit</button>

  </form>

  <!-- Employee Table -->
  <div class="table-wrapper" *ngIf="employees.length > 0">
    <h3>Employee List</h3>

    <table>
      <thead>
        <tr>
          <th>Employee ID</th>
          <th>Employee Name</th>
          <th>Address</th>
          <th>City</th>
          <th>Created By</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let emp of employees">
          <td>{{ emp.employeeId }}</td>
          <td>{{ emp.employeeName }}</td>
          <td>{{ emp.address }}</td>
          <td>{{ emp.city }}</td>
          <td>{{ emp.createdBy }}</td>
        </tr>
      </tbody>

    </table>
  </div>

</div>
