import { Component, EventEmitter, Output  } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import {FormAPIService} from '../../Services/form-api.service';

interface product {
  prodId: string;
  productName: string;
  ProductPrice: string;
  Qty: string;
  createdBy: string;
}


@Component({
  selector: 'app-product-form',
  templateUrl: './product-form.component.html',
  styleUrls: ['./product-form.component.scss']
})
export class ProductFormComponent {

@Output() next = new EventEmitter<void>();
@Output() prev = new EventEmitter<void>();

  productForm!: FormGroup;
  products: product[] = [];

  constructor(private fb: FormBuilder,private bindapi:FormAPIService) {}

  ngOnInit(): void {
    this.productForm = this.fb.group({
      prodId: ['', Validators.required],
      productName: ['', Validators.required],
      ProductPrice: ['', Validators.required],
      Qty: ['', Validators.required],
      createdBy: ['', Validators.required],
    });
    this.loadproducts();
  }

  submit() {
    // if (this.employeeForm.invalid) {
    //   this.employeeForm.markAllAsTouched();
    //   return;
    // }

    // const employeeData = this.employeeForm.value;
    // this.employees.push(employeeData);

    // this.employeeForm.reset();

    // this.bindapi.addproduct(this.productForm.value).subscribe({
    //   next: (res) => {
    //     console.log("Inserted Successfully", res);

    //     this.productForm.reset();

    //     this.loadproducts();  // Refresh table

    //     this.next.emit();    // Move to next form/step
    //   },
    //   error: (err) => {
    //     console.error("Error inserting product", err);
    //   }
    // });

  this.next.emit();

  }

  loadproducts() {
    this.bindapi.getproduct().subscribe({
      next: (data: any) => {
        this.products = data;
      },
      error: (err) => {
        console.error("Error loading products", err);
      }
    });
  }

goBack() {
  this.prev.emit();   
}

}
<div class="container">

  <!-- Employee Form -->
  <form [formGroup]="productForm" (ngSubmit)="submit()" class="employee-form">

    <h2>Product Details</h2>

    <div class="row">

      <div class="form-group">
        <label>Product ID</label>
        <input type="text" formControlName="prodId">
      </div>

      <div class="form-group">
        <label>Product Name</label>
        <input type="text" formControlName="ProductName">
      </div>

      <div class="form-group">
        <label>Product Price</label>
        <input type="text" formControlName="ProductPrice">
      </div>

      <div class="form-group">
        <label>Quantity</label>
        <input type="text" formControlName="Qty">
      </div>

      <div class="form-group">
        <label>Created By</label>
        <input type="text" formControlName="createdBy">
      </div>

    </div>

    <button type="submit" class="submit-btn">Submit</button>
 <button type="button" class="submit-btn" (click)="goBack()">Back</button>
  </form>

  <!-- Employee Table -->
  <div class="table-wrapper" *ngIf="products.length > 0">
    <h3>Product List</h3>

    <table>
      <thead>
        <tr>
          <th>Prod ID</th>
          <th>Poduuct Name</th>
          <th>Product Price</th>
          <th>Qty</th>
          <th>Created By</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let cli of products">
          <td>{{ cli.prodId }}</td>
          <td>{{ cli.productName }}</td>
          <td>{{ cli.ProductPrice }}</td>
          <td>{{ cli.Qty }}</td>
          <td>{{ cli.createdBy }}</td>
        </tr>
      </tbody>

    </table>
  </div>

</div>
