import { Component, EventEmitter, Output  } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import {FormAPIService} from '../../Services/form-api.service';

interface Client {
  clientId: string;
  clientName: string;
  clientlocation: string;
  contact: string;
  createdBy: string;
}

@Component({
  selector: 'app-client-form',
  templateUrl: './client-form.component.html',
  styleUrls: ['./client-form.component.scss']
})
export class ClientFormComponent {

@Output() next = new EventEmitter<void>();
@Output() prev = new EventEmitter<void>();

  clientForm!: FormGroup;
  clients: Client[] = [];

  constructor(private fb: FormBuilder,private bindapi:FormAPIService) {}

  ngOnInit(): void {
    this.clientForm = this.fb.group({
      clientId: ['', Validators.required],
      clientName: ['', Validators.required],
      clientlocation: ['', Validators.required],
      contact: ['', Validators.required],
      createdBy: ['', Validators.required],
    });
    this.loadClients();
  }

  //insert data api binding
  submit() {
      if (this.clientForm.invalid) {
      this.clientForm.markAllAsTouched();
      return;
    }

     const clientData = this.clientForm.value;

    this.bindapi.addclient(clientData).subscribe({
      next: (res) => {
        console.log("Inserted Successfully", res);

        this.clientForm.reset();

        this.loadClients();  // Refresh table

        this.next.emit();    // Move to next form/step
      },
      error: (err) => {
        console.error("Error inserting client", err);
      }
    });
  this.next.emit();

  }

  //table  data loading
 loadClients() {
    this.bindapi.getclient().subscribe({
      next: (data: any) => {
        this.clients = data;
      },
      error: (err) => {
        console.error("Error loading clients", err);
      }
    });
  }

goBack() {
  this.prev.emit();
}

}
<div class="container">

  <!-- Employee Form -->
  <form [formGroup]="clientForm" (ngSubmit)="submit()" class="employee-form">

    <h2>Client Details</h2>

    <div class="row">

      <div class="form-group">
        <label>Client ID</label>
        <input type="text" formControlName="clientId">
      </div>

      <div class="form-group">
        <label>Client Name</label>
        <input type="text" formControlName="clientName">
      </div>

      <div class="form-group">
        <label>Client Location</label>
        <input type="text" formControlName="clientcocation">
      </div>

      <div class="form-group">
        <label>Contact</label>
        <input type="text" formControlName="contact">
      </div>

      <div class="form-group">
        <label>Created By</label>
        <input type="text" formControlName="createdBy">
      </div>

    </div>

    <button type="submit" class="submit-btn">Submit</button>
 <button type="button" class="submit-btn" (click)="goBack()">Back</button>
  </form>

  <!-- Employee Table -->
  <div class="table-wrapper" *ngIf="clients.length > 0">
    <h3>Client List</h3>

    <table>
      <thead>
        <tr>
          <th>Client ID</th>
          <th>Client Name</th>
          <th>Client Location</th>
          <th>Contact</th>
          <th>Created By</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let cli of clients">
          <td>{{ cli.clientId }}</td>
          <td>{{ cli.clientName }}</td>
          <td>{{ cli.clientlocation }}</td>
          <td>{{ cli.contact }}</td>
          <td>{{ cli.createdBy }}</td>
        </tr>
      </tbody>

    </table>
  </div>

</div>
